function showMore(obj){
var oMore=document.getElementById(obj);
var oMoreUl=oMore.getElementsByTagName("ol")[0];
if(document.all){//ie
	oMore.setAttribute("onmouseover",eval(function(){oMoreUl.style.display="block";}));
	oMore.setAttribute("onmouseout",eval(function(){oMoreUl.style.display="none";}));
	oMoreUl.setAttribute("onmouseover",eval(function(){oMoreUl.style.display="block";}));
	oMoreUl.setAttribute("onmouseout",eval(function(){oMoreUl.style.display="none";}));
}else{//ff
	oMore.addEventListener("mouseover", function(){oMoreUl.style.display="block";}, false);
	oMore.addEventListener("mouseout", function(){oMoreUl.style.display="none";}, false);
	oMoreUl.addEventListener("mouseover", function(){oMoreUl.style.display="block";}, false);
	oMoreUl.addEventListener("mouseout", function(){oMoreUl.style.display="none";}, false);
}
}
